import {
    CLEAR_STATES,
    GET_POKEMONS,
    GET_POKEMON_ID,
    GET_POKEMON_NAME,
    GET_TYPES,
    POST_POKEMON,
    GET_POKEMONS_BY_TYPES,
    CREATED_OR_DB,
    ORDER_AZ,
    ORDER_STRENGTH,
} from "../actions";

//----------ESTADO INICIAL------------------
let initialState = {
    pokemons: [],
    allPokemons: [],
    pokemonDetail: [],
    types: [],
    pokemonsBuscados: null,
    pokemonsOrder: [],
}

//-----------------------REDUCER--------------------------
function rootReducer(state = initialState, action) {
    switch (action.type) {
        case GET_POKEMONS:
            console.log('Pokemones Cargados!')
            return {
                ...state,
                pokemons: action.payload,
                allPokemons: action.payload,
                pokemonsOrder: action.payload
            }
        case GET_POKEMON_ID:
            return {
                ...state,
                pokemonDetail: action.payload,
            }
        case GET_POKEMON_NAME:
            //console.log('Estoy en reducer: ', action.payload)    
            let poke = action.payload;
            //console.log(poke)
            return {
                ...state,
                pokemonsBuscados: poke
            }
        case GET_TYPES:
            return {
                ...state,
                types: action.payload,
            }
        case GET_POKEMONS_BY_TYPES:
            let type = action.payload;
            //console.log("Estoy en reducer " + type)
            let allPokemons = state.allPokemons;
            let pokesByTypes = type === 'allTypes' ?
                allPokemons :
                allPokemons.filter((poke) => poke.types.includes(type))
            //console.log(pokesByTypes)

            return {
                ...state,
                pokemons: pokesByTypes,
            }
        case CLEAR_STATES:
            console.log('Pokemons Re-Loaded')
            return {
                ...state,
                pokemonDetail: [],
                pokemonsBuscados: null,
                pokemons: state.allPokemons
            }
        case CREATED_OR_DB:
            //console.log('Estoy en reducer', action.payload)
            if (action.payload === 'allPokemons') {
                //console.log('ESTOY EN FILTRO ALLPOKES')

                return {
                    ...state,
                    pokemons: state.allPokemons
                }
            }
            if (action.payload === 'api') {
                //console.log('ESTOY EN FILTRO API')

                const allPokes = state.allPokemons;
                const respuesta = allPokes.filter((poke) => poke.id < 800)
                return {
                    ...state,
                    pokemons: respuesta,
                }
            }
            if (action.payload === 'DB') {
                //console.log('ESTOY EN FILTRO DB')

                const allPokes = state.allPokemons;
                const respuesta = allPokes.filter((poke) => poke.id.length > 15)
                //console.log(respuesta)
                return {
                    ...state,
                    pokemons: respuesta,
                }
            }
        case ORDER_AZ:
            if (action.payload === 'default') return { ...state, pokemons: state.allPokemons }
            let arrayNameOrder = action.payload === 'az' ? state.pokemonsOrder.sort((a, b) => {
                if (a.name > b.name) return 1;
                else if (b.name > a.name) return -1;
                else return 0;
            }) :
                state.pokemonsOrder.sort((a, b) => {
                    if (a.name > b.name) return -1;
                    else if (b.name > a.name) return 1;
                    else return 0;
                })

        case ORDER_STRENGTH:
            //console.log(state.pokemons[0])
            let arrayStrengthOrder = action.payload === 'strength+-' ? state.pokemons.sort((a, b) => {
                if (a.strength < b.strength) return 1;
                else if (b.strength < a.strength) return -1;
                else return 0;
            }) :
                state.pokemons.sort((a, b) => {
                    if (a.strength < b.strength) return -1;
                    else if (b.strength < a.strength) return 1;
                    else return 0;
                })
            return {
                ...state,
                pokemons: arrayStrengthOrder,
            }
        default:
            return state;
    }
}

export default rootReducer;
