import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTypes, clearStates, postPokemon } from "../../actions";
import { Link } from "react-router-dom";

export default function PokeCreate() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types); //selecciono state.types en types

  //--------------------CREO ESTADO LOCAL----------------------------
  const [newPoke, setNewPoke] = useState({
    name: "",
    hp: "",
    strenght: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
    img: "",
    types: [],
  });

  //--------------DISPATCH ACTIONS----------------------
  useEffect(() => {
    dispatch(getTypes());
  }, []);

  //---------------HANDLE BUTTON HOME-----------------
  const handleOnClickHome = () => {
    dispatch(clearStates());
  };

  return (
    <div>
      {/*---------BUTTON HOME-------------------- */}
      <Link to="/home">
        <button
          onClick={() => {
            handleOnClickHome();
          }}
        >
          Home
        </button>
      </Link>
      {/*-----------INPUT CREAT------------------------ */}
      <div className="form">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            console.log(newPoke);
            const respuesta = newPoke;

            //dispatch(postPokemon(newPoke));
          }}
        >
          <div>
            <input
              type="text"
              placeholder="Name..."
              value={newPoke.name}
              onChange={(e) => {
                const res =
                  e.target.value.toLowerCase().slice(0, 1).toUpperCase() +
                  e.target.value.toLowerCase().slice(1);
                setNewPoke({
                  ...newPoke,
                  name: res,
                });
              }}
            />
          </div>
          <div>
            <input
              type="number"
              placeholder="Life..."
              value={newPoke.hp}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 1
                  ? setNewPoke({
                      ...newPoke,
                      hp: res,
                    })
                  : alert("Limite excedido");
              }}
            />
          </div>
          <div>
            <input
              type="number"
              placeholder="Attack..."
              value={newPoke.strenght}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 1
                  ? setNewPoke({
                      ...newPoke,
                      strenght: res,
                    })
                  : alert("Limite excedido");
              }}
            />
          </div>
          <input type="submit" value="Poke Busqueda" />
        </form>
      </div>
      {/*-------------TYPES CHEKBOXS-------------------- */}
      <div className="types">
        {types.map((type) => {
          return (
            <div>
              <button>{type.name}</button>;
            </div>
          );
        })}
      </div>

      <div>
        <input type="submit" value="Create!" />
      </div>
    </div>
  );
}
