import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPokemonsId } from "../../actions";
import { Link } from "react-router-dom";

export default function PokeDetail({ id }) {
  const dispatch = useDispatch();
  const pokemonDetail = useSelector((state) => state.pokemonDetail);

  useEffect(() => {
    dispatch(getPokemonsId(id));
  }, []);

  //console.log(pokemonDetail);

  return (
    <div className="container">
      <div>
        <p>Name: {pokemonDetail.name}</p>
        <p>HP: {pokemonDetail.hp}</p>
        <p>Defense: {pokemonDetail.defense}</p>
        <p>Height: {pokemonDetail.height}</p>
        <p>Weight: {pokemonDetail.weight}</p>
        <p>Speed: {pokemonDetail.speed}</p>
        <p>Strength: {pokemonDetail.strength}</p>
        <img src={pokemonDetail.img} alt="" />
        <p>Types: {pokemonDetail.types.join(", ")}</p>
      </div>
    </div>
  );
}
