import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTypes, postPokemon } from "../../actions";
import { Link } from "react-router-dom";
import Pokemon from "../pokemon/Pokemon";

export default function PokeCreate() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types);

  const [pokemon, setPokemon] = useState({
    name: "",
    hp: "",
    stregth: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
    img: "",
  });

  useEffect(() => {
    dispatch(getTypes());
  }, []);

  const handleOnSubmit = (e) => {
    e.preventDefault();
    console.log(pokemon);
  };

  return (
    <div>
      <Link to="/home">
        <button>Home</button>
      </Link>

      <div className="create">
        <form onSubmit={(e) => handleOnSubmit(e)}>
          <input
            type="text"
            placeholder="name..."
            value={pokemon.name}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                name: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="HP..."
            value={pokemon.hp}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                hp: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Strength..."
            value={pokemon.stregth}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                strength: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Defense..."
            value={pokemon.defense}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                defense: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Speed..."
            value={pokemon.speed}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                speed: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Height..."
            value={pokemon.height}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                height: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Weight..."
            value={pokemon.weight}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                weight: e.target.value,
              })
            }
          />
          <input
            type="image"
            value={pokemon.img}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                img: e.target.value,
              })
            }
          />

          <input type="submit" value="Poke Busqueda" />
        </form>
      </div>
      <div>
        <div className="types">
          {types.map((type) => {
            return <button>{type.name}</button>;
          })}
        </div>
      </div>
    </div>
  );
}
