import { GET_POKEMONS } from "../actions";
import { GET_POKEMON_ID } from "../actions";
import { GET_POKEMON_NAME } from "../actions";
import { GET_TYPES } from "../actions";
import { POST_POKEMON } from "../actions";
import { GET_POKEMONS_BY_TYPE } from '../actions'

let initialState = {
    pokemons: [],
    pokemonDetail: [],
    types: [],
    pokemonsByTypes: [],
}

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload
            }
        case GET_POKEMON_ID:
            return {
                ...state,
                pokemonDetail: action.payload,
            }
        case GET_POKEMON_NAME:
            return {
                ...state,
                pokemonDetail: action.payload
            }
        case GET_TYPES:
            return {
                ...state,
                types: action.payload,
            }
        case GET_POKEMONS_BY_TYPE:

            const type = action.payload;
            const pokesByType = state.pokemons.filter((pokes) => pokes.types.includes(type))

            return {
                ...state,
                pokemonsByTypes: pokesByType,
            }
        default:
            return state;
    }
}

export default rootReducer;
