import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import {
  getPokemons,
  getTypes,
  getPokemonsByTypes,
  getPokemonName,
} from "../../actions";
import Pokemons from "../pokemons/Pokemons.jsx";
import Paginado from "../paginado/Paginado.jsx";
import "./Home.css";

export default function Home() {
  const dispatch = useDispatch(); //Guardo el dispatch en la constante para poder despachar las actions donde quiera
  const types = useSelector((state) => state.types); //me guardo el array types del estado
  const pokemons = useSelector((state) => state.pokemons); //me guardo el array pokemmons del estado (tiene todos los pokemons)

  //------------------------------PAGINADO-----------------------------------------------------
  const [currentPage, setCurrentPage] = useState(1); //Estado Local de paginado
  const [pokemonsPerPage, setPokemonsPerPage] = useState(9); //Digo en parametros de useState cuantos personajes por pagina quiero
  const indexLastPokemon = currentPage * pokemonsPerPage; //Ultimo poke en la pagina. (Pagina 1 seria index 9)
  const indexFirstPokemon = indexLastPokemon - pokemonsPerPage; //Index del primer poke de la pagina
  const currentPokemons = pokemons.slice(indexFirstPokemon, indexLastPokemon); //Selecciono la cantidad de pokemons que quiero que se muestren con los index
  const paginado = (nroPag) => {
    setCurrentPage(nroPag);
  };

  //----------------------------SEARCHBAR---------------------------------------------------
  const [pokemon, setPokemon] = useState();//ESTADO LOCAL



  useEffect(() => {
    //Aca despacho las 2 actions para que cuando se me cargue la pag los tenga en el estado
    dispatch(getTypes());
    dispatch(getPokemons());
  }, []);

  useEffect(() => console.log("HOME MONTADO"));

  const handleOnChangeTypes = (e) => {
    //Funcion onChange de SelectFiltrosTypes
    //console.log(e.target.value);
    dispatch(getPokemonsByTypes(e.target.value));
  };

  return (
    <div className="container">
      <div className="nav">
        <Link to="/">
          <button>Leave</button>
        </Link>
        <Link to="/home">
          <button>Home</button>
        </Link>
        <div className="searchBar">
          <form
            onSubmit={(e) => {
              e.preventDefault();
              //console.log(pokemon);
              dispatch(getPokemonName(pokemon));
            }}
          >
            <input
              type="text"
              placeholder="pokemon..."
              value={pokemon}
              onChange={(e) => setPokemon(e.target.value)}
            />
            <input type="submit" value="Poke Busqueda" />
          </form>
        </div>
        <Link to="/create">
          <button>Create</button>
        </Link>
        <div className="orden">
          <p>Orden: </p>
          <select name="orden">
            <option value="a-z">A - Z</option>
            <option value="z-a">Z - A</option>
            <option value="strength+-">↑Fuerza - ↓Fuerza</option>
            <option value="strength-+">↓Fuerza - ↑Fuerza</option>
          </select>
        </div>
        <div className="filtros">
          <p>Filtros: </p>
          <select name="types" onChange={(e) => handleOnChangeTypes(e)}>
            <option value="allTypes">allTypes</option>
            {types.map((type) => {
              return (
                <option value={`${type.name}`} key={type}>
                  {type.name}
                </option>
              );
            })}
          </select>
          <select>
            <option value="allPokemons">allPokemons</option>
            <option value="created">Created</option>
            <option value="Exists">Exists</option>
          </select>
        </div>
      </div>
      <Paginado
        pokemonsPerPage={pokemonsPerPage}
        pokemons={pokemons.length}
        paginado={paginado}
      />
      <div className="pokemones">
        <Pokemons currentPokemons={currentPokemons} />
      </div>
    </div>
  );
}
