import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTypes, clearStates } from "../../actions";
import { Link } from "react-router-dom";

export default function PokeCreate() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types);

  useEffect(() => {
    dispatch(getTypes());
  }, []);
//---------------
  const handleOnClickHome = () => {
    dispatch(clearStates());
  };

  return (
    <div>
      <Link to="/home">
        <button
          onClick={() => {
            handleOnClickHome();
          }}
        >
          Home
        </button>
      </Link>

      <div className="form">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            //console.log(pokemon);
            const respuesta = pokemonName.toLowerCase();
            dispatch(getPokemonName(respuesta));
          }}
        >
          <input
            type="text"
            placeholder="pokemon..."
            value={pokemonName}
            onChange={(e) => setPokemonName(e.target.value)}
          />
          <input type="submit" value="Poke Busqueda" />
        </form>
        <input type="text" placeholder="Name" />
        <input type="text" placeholder="HP" />
        <input type="text" placeholder="Strength" />
        <input type="text" placeholder="Defense" />
        <input type="text" placeholder="Speed" />
        <input type="text" placeholder="Height" />
        <input type="text" placeholder="Weight" />
        <input type="text" placeholder="img" />
      </div>
      <div className="types">
        {types.map((type) => {
          return <button>{type.name}</button>;
        })}
      </div>

      <div>
        <input type="submit" value="Create!" />
      </div>
    </div>
  );
}
