import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPokemonsId, clearStates, getPokemons } from "../../actions";
import { Link } from "react-router-dom";
import style from "./PokeDetail.module.css";

export default function PokeDetail({ id }) {
  const dispatch = useDispatch();
  const pokemonDetail = useSelector((state) => state.pokemonDetail);

  useEffect(() => {
    dispatch(getPokemonsId(id));
    dispatch(clearStates());
    dispatch(getPokemons());
  }, []);

  console.log(pokemonDetail.types);

  let nuevosTypes = [];
  if (id > 150) {
    console.log("Entre?");
    pokemonDetail.types?.filter((type) => nuevosTypes.push(type.name));
    //console.log(nuevosTypes);
  }

  return (
    <div class={style.container}>
      <Link to="/home">
        <button class={style.buttonHome}>Go Home</button>
      </Link>
      <div class={style.info}>
        <h1 class={style.name}>{pokemonDetail.name}</h1>
        <div class={style.hp}>‚ù§Ô∏è {pokemonDetail.hp}</div>
        <div class={style.defense}>üõ° {pokemonDetail.defense}</div>
        <div class={style.height}>‚öñÔ∏è {pokemonDetail.height}</div>
        <div class={style.weight}>Weight: {pokemonDetail.weight}</div>
        <div class={style.speed}>Speed: {pokemonDetail.speed}</div>
        <div class={style.strength}>Attack: {pokemonDetail.strength}</div>
        <img src={pokemonDetail.img} alt="" class={style.img} />
        <div class={style.info_types}>Types: {}</div>
      </div>
    </div>
  );
}
