import axios from 'axios';

export const GET_POKEMONS = 'GET_POKEMONS';
export const GET_POKEMON_ID = 'GET_POKEMON_ID';
export const GET_POKEMON_NAME = 'GET_POKEMON_NAME';
export const GET_TYPES = 'GET_TYPES'
export const POST_POKEMON = 'POST_POKEMON';
export const GET_POKEMONS_BY_TYPES = 'GET_POKEMONS_BY_TYPES'
export const CLEAR_SEARCH = 'CLEAR_SEARCH';

export function getPokemons() {
    console.log('Buscando Pokes')
    return async function (dispatch) {

        const pokes = (await axios.get('http://localhost:3001/pokemons')).data;

        return dispatch({
            type: GET_POKEMONS,
            payload: pokes//Arreglo con pokemons del BackEnd
        })
    }
}

export function getPokemonsId(id) {
    return async function (dispatch) {

        const pokeId = (await axios.get(`http://localhost:3001/pokemons/${id}`)).data;

        return dispatch({
            type: GET_POKEMON_ID,
            payload: pokeId//Arreglo con detalles del poke id]
        })
    }
}

export function getPokemonName(name) {
    return async function (dispatch) {
        try {
            //console.log(name)
            const pokeName = (await axios.get(`http://localhost:3001/pokemons/?name=${name}`)).data;
            return dispatch({
                type: GET_POKEMON_NAME,
                payload: pokeName //Arreglo con detalle del poke Name
            })
        } catch {
            console.log('Pokemon no encontrado')
        }
    }
}

export function getTypes() {
    return async function (dispatch) {
        const types = (await axios.get('http://localhost:3001/types')).data;

        return dispatch({
            type: GET_TYPES,
            payload: types, //Arreglo con types
        })
    }
}

export function postPokemon({ name, hp, height, weight, strength, types }) {//Destructuring del objeto que te pasan
    //Crear poke en la DB
    return async function (dispatch) {
        const pokeCreado = (await axios.post('http://localhost:3001/pokemons')).data;
    }
}

export function getPokemonsByTypes(filterType) {
    return function (dispatch) {

        return dispatch({
            type: GET_POKEMONS_BY_TYPES,
            payload: filterType,
        })
    }
}

export function clearSearch() {
    return function (dispatch) {
        return dispatch({
            type: CLEAR_SEARCH,
        })
    }
}
