import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTypes } from "../../actions";
import { Link } from "react-router-dom";

export default function PokeCreate() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types);

  useEffect(() => {
    dispatch(getTypes());
  }, []);

  return (
    <div>
      <Link to="/home">
        <button>Home</button>
      </Link>


      <div className="create">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            //console.log(pokemon);
            dispatch(post(pokemon));
          }}
        >
          <input
            type="text"
            placeholder="pokemon..."
            value={pokemon}
            onChange={(e) => setPokemon(e.target.value)}
          />
          <input type="submit" value="Poke Busqueda" />
        </form>


      </div>
      <div>
        <input type="text" placeholder="Name" />
        <input type="text" placeholder="HP" />
        <input type="text" placeholder="Strength" />
        <input type="text" placeholder="Defense" />
        <input type="text" placeholder="Speed" />
        <input type="text" placeholder="Height" />
        <input type="text" placeholder="Weight" />
        <input type="text" placeholder="img" />
        <div className="types">
          {types.map((type) => {
            return <button>{type.name}</button>;
          })}
        </div>
      </div>

      <div>
        <input type="submit" value="Create!" />
      </div>
    </div>
  );
}
