import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPokemonsId, clearStates, getPokemons } from "../../actions";
import { Link } from "react-router-dom";
import style from "./PokeDetail.module.css";

export default function PokeDetail({ id }) {
  const dispatch = useDispatch();
  const pokemonDetail = useSelector((state) => state.pokemonDetail);

  useEffect(() => {
    dispatch(getPokemonsId(id));
    dispatch(clearStates());
    dispatch(getPokemons());
  }, []);

  console.log(pokemonDetail.types);

  let nuevosTypes = [];
  if (id > 150) {
    console.log("Entre?");
    pokemonDetail.types?.filter((type) => nuevosTypes.push(type.name));
    //console.log(nuevosTypes);
  }

  return (
    <div class={style.container}>
      <Link to="/home">
        <button class={style.buttonHome}>Go Home</button>
      </Link>
      <div className={style.info}>
        <div class={style.infoPrincipal}>
          <h1 class={style.name}>{pokemonDetail.name}</h1>
          <img src={pokemonDetail.img} alt="" class={style.img} />
          <h3 class={style.types}>
            {id > 150
              ? nuevosTypes?.join(", ")
              : pokemonDetail.types?.join(", ")}
          </h3>
        </div>
        <div className={style.infoSecundaria}>
          <div class={style.hp}>â¤ï¸ {pokemonDetail.hp}</div>
          <div class={style.defense}>ğŸ›¡ {pokemonDetail.defense}</div>
          <div class={style.height}>ğŸ“ {pokemonDetail.height} </div>
          <div class={style.weight}>âš–ï¸ {pokemonDetail.weight}</div>
          <div class={style.speed}>â±ï¸ {pokemonDetail.speed}</div>
          <div class={style.strength}>ğŸ’ª {pokemonDetail.strength}</div>
        </div>
      </div>
    </div>
  );
}
