import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getPokemons, getTypes } from "../../actions";
import Pokemons from "../pokemons/Pokemons";

export default function Home() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types);

  const [currentPage, setCurrentPage] = useState(1); //Estado Local de paginado
  const [pokemonsPerPage, setPokemonsPerPage] = useState(9); //Digo en parametros de useState cuantos personajes por pagina quiero
  const indexLastPokemon = currentPage * pokemonsPerPage; //Ultimo poke en la pagina. (Pagina 1 seria index 9)
  const indexFirstPokemon = indexLastPokemon - pokemonsPerPage;//Index del primer poke de la pagina 
  const currentPokemons = //Los pokemones que estan en la pagina 


  useEffect(() => {
    dispatch(getTypes());
    dispatch(getPokemons());
  }, []);

  useEffect(() => console.log("HOME MONTADO"));

  return (
    <div className="container">
      <div className="nav">
        <Link to="/">
          <button>Leave</button>
        </Link>
        <Link to="/home">
          <button>Home</button>
        </Link>
        <input type="text" placeholder="pokemon..." value="" onChange="" />
        <input type="submit" value="Poke Busqueda" />
        <Link to="/create">
          <button>Create</button>
        </Link>
        <div className="Orden">
          <p>Orden: </p>
          <select name="orden">
            <option value="a-z">A - Z</option>
            <option value="z-a">Z - A</option>
            <option value="strength+-">↑Fuerza - ↓Fuerza</option>
            <option value="strength-+">↓Fuerza - ↑Fuerza</option>
          </select>
        </div>
        <div className="Filtros">
          <p>Filtros: </p>
          <select name="types">
            {types.map((type) => {
              return <option value={`${type.name}`}>{type.name}</option>;
            })}
          </select>
          <select>
            <option value="api">Api</option>
            <option value="creados">Creados</option>
          </select>
        </div>
      </div>
      <div className="pokemones">
        <Pokemons />
      </div>
    </div>
  );
}
