import { GET_POKEMONS } from "../actions";
import { GET_POKEMON_ID } from "../actions";
import { GET_POKEMON_NAME } from "../actions";
import { GET_TYPES } from "../actions";
import { POST_POKEMON } from "../actions";
import { GET_POKEMONS_BY_TYPES } from '../actions'

let initialState = {
    pokemons: [],
    allPokemons: [],
    pokemonDetail: [],
    types: [],
}

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                allPokemons: action.payload,
            }
        case GET_POKEMON_ID:x   
            return {
                ...state,
                pokemonDetail: action.payload,
            }
        case GET_POKEMON_NAME:
            //console.log('Estoy en reducer: ', action.payload)    4
            let poke = action.payload;
            let respuesta = poke === 'undefined' ? allPokemons : poke
            return {
                ...state,
                pokemons: respuesta
            }
        case GET_TYPES:
            return {
                ...state,
                types: action.payload,
            }
        case GET_POKEMONS_BY_TYPES:
            let type = action.payload;
            let allPokemons = state.allPokemons;
            let pokesByTypes = type === 'allTypes' ?
                allPokemons :
                allPokemons.filter((pokes) => pokes.types.includes(type))

            return {
                ...state,
                pokemons: pokesByTypes,
            }
        default:
            return state;
    }
}

export default rootReducer;
