import {
    CLEAR_STATES,
    GET_POKEMONS,
    GET_POKEMON_ID,
    GET_POKEMON_NAME,
    GET_TYPES,
    POST_POKEMON,
    GET_POKEMONS_BY_TYPES,
    CREATED_OR_DB
} from "../actions";

//----------ESTADO INICIAL------------------
let initialState = {
    pokemons: [],
    allPokemons: [],
    pokemonDetail: [],
    types: [],
    pokemonsBuscados: null,
}

//-----------------------REDUCER--------------------------
function rootReducer(state = initialState, action) {
    switch (action.type) {
        case GET_POKEMONS:
            return {
