import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTypes, clearStates, postPokemon } from "../../actions";
import { Link } from "react-router-dom";
import "./PokeCreate.css";
import swal from "sweetalert";

export default function PokeCreate() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types); //selecciono state.types en types
  const pokemons = useSelector((state) => state.pokemons);

  //--------------------CREO ESTADO LOCAL----------------------------
  const [newPoke, setNewPoke] = useState({
    name: "",
    hp: "",
    strenght: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
    img: "",
    types: [],
  });

  //------------------USE EFFECT--------------------------
  useEffect(() => {
    dispatch(getTypes()); //Me traigo los types para poder usarlos
  }, []);

  //---------------HANDLE BUTTON HOME-----------------
  const handleOnClickHome = () => {
    dispatch(clearStates());
  };

  return (
    <div class="container">
      {/*---------BUTTON HOME-------------------- */}
      <Link to="/home">
        <button
          onClick={() => {
            handleOnClickHome();
          }}
          class="button"
        >
          Home
        </button>
      </Link>
      {/*-----------INPUT CREAT------------------------ */}
      <div class="form">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            console.log(newPoke);
            dispatch(postPokemon(newPoke));
            setNewPoke({
              name: "",
              hp: "",
              strenght: "",
              defense: "",
              speed: "",
              height: "",
              weight: "",
              img: "",
              types: [],
            }); //Borro los campos
          }}
        >
          <div>
            <input
              type="text"
              placeholder="Name..."
              value={newPoke.name}
              onChange={(e) => {
                const res = e.target.value.toLowerCase();
                setNewPoke({
                  ...newPoke,
                  name: res,
                });
              }}
            />
          </div>
          <div>
            <input
              type="number"
              placeholder="Life..."
              value={newPoke.hp}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      hp: res,
                    })
                  : swal("Only select 2 Types!", "", "error");
              }}
            />
          </div>
          <div>
            <input
              type="number"
              placeholder="Attack..."
              value={newPoke.strenght}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      strenght: res,
                    })
                  : swal("Only select 2 Types!", "", "error");
              }}
            />
          </div>
          <div>
            <input
              type="number"
              placeholder="Defense..."
              value={newPoke.defense}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      defense: res,
                    })
                  : swal("Only select 2 Types!", "", "error");
              }}
            />
          </div>
          <div>
            <input
              type="number"
              placeholder="Speed..."
              value={newPoke.speed}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      speed: res,
                    })
                  : swal("Only select 2 Types!", "", "error");
              }}
            />
          </div>
          <div>
            <input
              type="number"
              placeholder="Height..."
              value={newPoke.height}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      height: res,
                    })
                  : swal("Only select 2 Types!", "", "error");
              }}
            />
          </div>
          <div>
            <input
              type="number"
              placeholder="Weight..."
              value={newPoke.weight}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      weight: res,
                    })
                  : swal("Only select 2 Types!", "", "error");
              }}
            />
          </div>
          <div>
            <input
              type="url"
              placeholder="Image URL..."
              value={newPoke.img}
              attern="https://.*"
              onChange={(e) => {
                const res = e.target.value;
                setNewPoke({
                  ...newPoke,
                  img: res,
                });
              }}
            />
          </div>

          <Link to="/home">
            <div class="submitButton">
              <input type="submit" value="Add To PokeFamily!" />
            </div>
          </Link>
        </form>
      </div>
      {/*-------------TYPES CHEKBOXS-------------------- */}

      <div class="checkType">
        {types.map((type, index) => (
          <label>
            <div>
              {type.name + ":"}
              <input
                type="checkbox"
                id={index + 1}
                value={type.name}
                onChange={(e) =>
                  newPoke.types.length !== 2
                    ? setNewPoke({
                        ...newPoke,
                        types: [...newPoke.types, e.target.id],
                      })
                    : swal("Only select 2 Types!", "", "error")
                }
              />
            </div>
          </label>
        ))}
      </div>
    </div>
  );
}
