import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTypes, clearStates, postPokemon } from "../../actions";
import { Link } from "react-router-dom";
import style from "./PokeCreate.module.css";
import fondo from "../../assets/create.jpg";

export default function PokeCreate() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types); //selecciono state.types en types
  const pokemons = useSelector((state) => state.pokemons);

  //--------------------CREO ESTADO LOCAL----------------------------
  const [newPoke, setNewPoke] = useState({
    name: "",
    hp: "",
    strength: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
    img: "",
    types: [],
  });

  //------------------USE EFFECT--------------------------
  useEffect(() => {
    dispatch(getTypes()); //Me traigo los types para poder usarlos
  }, []);

  //---------------HANDLE BUTTON HOME-----------------
  const handleOnClickHome = () => {
    dispatch(clearStates());
  };

  const showtype = (num) => {
    types.find((type) => type.id === num);
  };

  // [{ id: 1, name: normal } {}{}{}]

  return (
    <div class={style.container}>
      {/*---------BUTTON HOME-------------------- */}
      <Link to="/home">
        <button
          class={style.buttonHome}
          onClick={() => {
            handleOnClickHome();
          }}
        >
          Home
        </button>
      </Link>
      {/*-----------INPUT CREAT------------------------ */}
      <div class={style.formContainer}>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            console.log(newPoke);
            dispatch(postPokemon(newPoke));
            setNewPoke({
              name: "",
              hp: "",
              strength: "",
              defense: "",
              speed: "",
              height: "",
              weight: "",
              img: "",
              types: [],
            }); //Borro los campos
            alert("Pokemon Succesfully Created!");
          }}
        >
          <div className={style.input}>
            <input
              className={style.inp}
              type="text"
              placeholder="Name..."
              value={newPoke.name}
              onChange={(e) => {
                const res = e.target.value.toLowerCase();
                setNewPoke({
                  ...newPoke,
                  name: res,
                });
              }}
            />
          </div>
          <div className={style.input}>
            <input
              className={style.inp}
              type="number"
              placeholder="Life..."
              value={newPoke.hp}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      hp: res,
                    })
                  : alert("Select a number from 1-100");
              }}
            />
          </div>

          <div className={style.input}>
            <input
              className={style.inp}
              type="number"
              placeholder="Attack..."
              value={newPoke.strength}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      strength: res,
                    })
                  : alert("Select a number from 1-100");
              }}
            />
          </div>

          <div className={style.input}>
            <input
              className={style.inp}
              type="number"
              placeholder="Defense..."
              value={newPoke.defense}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      defense: res,
                    })
                  : alert("Select a number from 1-100");
              }}
            />
          </div>

          <div className={style.input}>
            <input
              className={style.inp}
              type="number"
              placeholder="Speed..."
              value={newPoke.speed}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      speed: res,
                    })
                  : alert("Select a number from 1-100");
              }}
            />
          </div>

          <div className={style.input}>
            <input
              className={style.inp}
              type="number"
              placeholder="Height..."
              value={newPoke.height}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      height: res,
                    })
                  : alert("Select a number from 1-100");
              }}
            />
          </div>

          <div className={style.input}>
            <input
              className={style.inp}
              type="number"
              placeholder="Weight..."
              value={newPoke.weight}
              onChange={(e) => {
                const res = e.target.value;
                res <= 100 && res >= 0
                  ? setNewPoke({
                      ...newPoke,
                      weight: res,
                    })
                  : alert("Select a number from 1-100");
              }}
            />
          </div>

          <div className={style.input}>
            {" "}
            <input
              className={style.inp}
              type="url"
              placeholder="Image URL..."
              value={newPoke.img}
              attern="https://.*"
              onChange={(e) => {
                const res = e.target.value;
                setNewPoke({
                  ...newPoke,
                  img: res,
                });
              }}
            />
          </div>
          {/*---------------------------ARRANCAN SELECTOR TYPES--------------------------------- */}
          <div className={style.types}>
            <select
              name="Types1"
              className={style.select}
              onChange={(e) => {
                let type = e.target.value;
                newPoke.types.includes(type) === false
                  ? newPoke.types.length !== 2
                    ? setNewPoke({
                        ...newPoke,
                        types: [...newPoke.types, type],
                      })
                    : alert("Only select 2 types!")
                  : alert("Select a different Type!");
              }}
            >
              <option>Type 1</option>
              {types.map((type, index) => {
                return (
                  <option
                    className={style.option}
                    id={index + 1}
                    value={index + 1}
                  >
                    {type.name}
                  </option>
                );
              })}
            </select>
            <select
              name="Types2"
              className={style.select}
              onChange={(e) => {
                let type = e.target.value;
                newPoke.types.includes(type) === false
                  ? setNewPoke({
                      ...newPoke,
                      types: [...newPoke.types, type],
                    })
                  : alert("Select a different type!");
              }}
            >
              <option>Type 2</option>
              {types.map((type, index) => {
                return (
                  <option
                    className={style.option}
                    id={index + 1}
                    value={index + 1}
                  >
                    {type.name}
                  </option>
                );
              })}
            </select>
          </div>
          <div className={style.submitButton}>
            <input
              type="submit"
              value="Add To PokeFamily!"
              className={style.inpSubmit}
            />
          </div>
        </form>
      </div>
      <div className={style.typesMuestra}>
        {newPoke.types?.length > 0
          ? newPoke.types.map((type) => {
              return <p>{showTypes(type)}</p>;
            })
          : null}
      </div>
      <img src={fondo} alt="" className={style.fondo} />
    </div>
  );
}

