import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPokemonsId } from "../../actions";

export default function PokeDetail({ id }) {
  const dispatch = useDispatch();
  const pokemonDetail = useSelector((state) => state.pokemonDetail);

  useEffect(() => {
    dispatch(getPokemonsId(id));
  }, []);

  console.log(pokemonDetail);

  return (
    <div className="container">
      <div>
        <p>{pokemonDetail.name}</p>
        <p>{pokemonDetail.hp}</p>
        <p>{pokemonDetail.defense}</p>
        <p>{pokemonDetail.height}</p>
        <p>{pokemonDetail.weight}</p>
        <p>{pokemonDetail.speed}</p>
        <p>{pokemonDetail.strength}</p>
        <img src={pokemonDetail.img} alt="" />
      </div>
    </div>
  );
}
