import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTypes, postPokemon } from "../../actions";
import { Link } from "react-router-dom";
import Pokemon from "../pokemon/Pokemon";

export default function PokeCreate() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types);

  const [pokemon, setPokemon] = useState();

  useEffect(() => {
    dispatch(getTypes());
  }, []);

  return (
    <div>
      <Link to="/home">
        <button>Home</button>
      </Link>

      <div className="create">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            //console.log(pokemon);
            dispatch(postPokemon());
          }}
        >
          <input
            type="text"
            placeholder="name..."
            value={pokemon}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                name: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="HP..."
            value={pokemon}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                hp: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Strength..."
            value={pokemon}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                strength: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Defense..."
            value={pokemon}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                defense: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Speed..."
            value={pokemon}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                speed: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Height..."
            value={pokemon}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                height: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Weight..."
            value={pokemon}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                weight: e.target.value,
              })
            }
          />
          <input
            type="image"
            value={pokemon}
            onChange={(e) =>
              setPokemon({
                ...Pokemon,
                img: e.target.value,
              })
            }
          />

          <input type="submit" value="Poke Create" />
        </form>
      </div>
      <div>
        <div className="types">
          {types.map((type) => {
            return <button>{type.name}</button>;
          })}
        </div>
      </div>

      <div>
        <input type="submit" value="Create!" />
      </div>
    </div>
  );
}
