import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTypes, postPokemon } from "../../actions";
import { Link } from "react-router-dom";
import "./PokeCreate.css";

export default function PokeCreate() {
  const dispatch = useDispatch();
  const types = useSelector((state) => state.types);

  const [pokemon, setPokemon] = useState({
    name: "",
    hp: "",
    strength: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
    img: "",
    types: [],
  });

  useEffect(() => {
    dispatch(getTypes());
  }, []);

  const handleOnSubmit = (e) => {
    e.preventDefault();
    console.log(pokemon);
    dispatch(postPokemon(pokemon));
  };

  return (
    <div>
      <Link to="/home">
        <button>Home</button>
      </Link>

      <div className="create">
        <form onSubmit={(e) => handleOnSubmit(e)}>
          <input
            type="text"
            placeholder="name..."
            value={pokemon.name}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                name: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="HP..."
            value={pokemon.hp}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                hp: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Strength..."
            value={pokemon.strength}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                strength: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Defense..."
            value={pokemon.defense}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                defense: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Speed..."
            value={pokemon.speed}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                speed: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Height..."
            value={pokemon.height}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                height: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Weight..."
            value={pokemon.weight}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                weight: e.target.value,
              })
            }
          />
          <input
            type="image"
            value={pokemon.img}
            onChange={(e) =>
              setPokemon({
                ...pokemon,
                img: e.target.value,
              })
            }
          />

          <input type="submit" value="Crear" />
        </form>
      </div>
      {types.map((type, index) => (
        <div>
          <input
            type="checkbox"
            id={index}
            value={index}
            onChange={(e) =>
              console.log()
              //setPokemon({ ...pokemon, types: [...pokemon.types, type.name] })
            }
          />
          <span className="checkType">{type.name}</span>
        </div>
      ))}
    </div>
  );
}
